import React, { useState } from "react";
import {
  Box,
  TextField,
  InputAdornment,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Typography,
  Grid,
  Card,
} from "@mui/material";
import SearchIcon from "@mui/icons-material/Search";
import value from "../data.json";
// import data from "../data.json";

const Home = () => {
  const data = ["Polar", "Africa", "Europe", "Americas", "Oceania", "Asia"];

  const [search, setSearch] = useState();
  const [filteredResults, setFilteredResults] = useState([]);
  console.log(filteredResults, "filtered");

  console.log(search, "search");

  const Handlesearch = (searchValue) => {
    // e.target.value;
    setSearch(searchValue);
    if (search !== "") {
      const filterData = value.filter((item) => {
        return Object.values(item)
          .join("")
          .toLowerCase()
          .includes(search.toLowerCase());
      });
      setFilteredResults(filterData);
    }
  };

  return (
    <Box>
      <Box
        display={"flex"}
        justifyContent={"space-around"}
        marginBottom={5}
        marginTop={2}
      >
        <TextField
          type="text"
          name="search"
          onChange={(e) => Handlesearch(e.target.value)}
          InputProps={{
            startAdornment: (
              <InputAdornment
                position="start"
                // onClick={handleClickShowPassword}
              >
                <SearchIcon />
              </InputAdornment>
            ),
          }}
          placeholder="search country"
        />
        <Box sx={{ minWidth: 120 }}>
          <FormControl fullWidth>
            <InputLabel id="demo-simple-select-label">Country</InputLabel>
            <Select
              labelId="demo-simple-select-label"
              id="demo-simple-select"
              //   value={age}
              label="country"
              //   onChange={handleChange}
            >
              {data?.map((value) => {
                return (
                  <div>
                    <MenuItem>{value}</MenuItem>
                  </div>
                );
              })}
            </Select>
          </FormControl>
        </Box>
      </Box>
      <Box>
        <Grid
          container
          spacing={{ xs: 2, md: 3 }}
          columns={{ xs: 4, sm: 8, md: 12 }}
        >
          {value?.map((value, index) => (
            <Grid item xs={2} sm={4} md={4} key={index}>
              <Card
                sx={{
                  width: "350px",
                  height: "420px",
                  marginLeft: "30px",
                  marginTop: "30px",
                }}
              >
                <img
                  src={value.flags.svg}
                  alt={value.name}
                  width={350}
                  height={250}
                />

                <Typography marginLeft={3} fontWeight={500} variant="h6">
                  {value.name}
                </Typography>
                <Box display={"flex"} marginLeft={3} marginTop={3}>
                  <label>population :</label>
                  <Typography>{value.population}</Typography>
                </Box>

                <Box display={"flex"} marginLeft={3}>
                  <label>region :</label>
                  <Typography>{value.region}</Typography>
                </Box>
                <Box display={"flex"} marginLeft={3}>
                  <label>capital :</label>
                  <Typography>{value.capital}</Typography>
                </Box>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Box>
  );
};

export default Home;
